<script>
import { requestApi } from '@/request/request'
export default {
  props: ['goodsData', 'salesVolume'],
  data () {
    return {
      gid: '',
      key: '',
      Fiexd: false,
      purInfo: [], // 订单
      purInfoText: [], //
      marqueeShow: true
    }
  },
  mounted () {
    if (this.$route.query.gid) {
      this.gid = this.$route.query.gid
      this.key = this.$route.query.key
    } else if (this.$route.params.gid) {
      this.gid = this.$route.params.gid
      this.key = this.$route.params.key
    }
    // window.addEventListener('scroll', this.handleScroll)
    this.getPurInfo()
  },
  destroyed () {
    // window.removeEventListener('scroll', this.handleScroll)
  },
  methods: {
    getPurInfo () {
      requestApi({
        name: 'getPurInfo'
      }).then(res => {
        this.purInfo = res.data
        this.purInfoText = this.purInfo[0]
        this.timerHandler()
      })
    },
    // handleScroll () {
    //   let scrollTop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop
    //   if (scrollTop > 400) {
    //     this.Fiexd = true
    //   } else {
    //     this.Fiexd = false
    //   }
    // },
    timerHandler () {
      let timer = 0
      this.timers = setInterval(() => {
        timer++
        if (timer === 4) {
          this.marqueeShow = false
        } else if (timer === 5) {
          this.marqueeShow = false
          clearInterval(this.timers)
          let purInfoText = Math.floor(Math.random() * this.purInfo.length)
          this.purInfoText = this.purInfo[purInfoText]
          this.timerHandler()
        } else {
          this.marqueeShow = true
        }
      }, 1000)
    },
    //  常见帮助
    toHelp () {
      this.$emit('handleHelp')
    }
  }

}
</script>

