    imgUrl: shareData.imgUrl, // 分享图标
    success: function () {
      // 用户点击了分享后执行的回调函数
    }
  })
}

export function openAddress () {
  console.log('获取微信地址')
  return new Promise((resolve, reject) => {
    wx.openAddress({
      success: function (res) {
        console.log('微信地址', res)
        resolve(res)
      },
      cancel: function (errMsg) {
        // 用户取消拉出地址 //
        console.log('失败', errMsg)
        resolve(errMsg)
      }
    })
  })
}
